mapper.select:

select

  distinct


from t_production_dataprediction_detail

  


  order by ${orderByClause}


  limit #{page.begin} , #{page.length}


select 

from t_production_dataprediction_detail
where MONTH = #{month,jdbcType=VARCHAR}
  and ptype = #{ptype,jdbcType=VARCHAR}
  and subject_parent_name = #{subjectParentName,jdbcType=VARCHAR}
  and year_num = #{yearNum,jdbcType=VARCHAR}

mapper.delete:

delete from t_production_dataprediction_detail
where MONTH = #{month,jdbcType=VARCHAR}
  and ptype = #{ptype,jdbcType=VARCHAR}
  and subject_parent_name = #{subjectParentName,jdbcType=VARCHAR}
  and year_num = #{yearNum,jdbcType=VARCHAR}

mapper.update:

update t_production_dataprediction_detail
set id = #{id,jdbcType=BIGINT},
  pamount = #{pamount,jdbcType=VARCHAR},
  subjec_parent_code = #{subjecParentCode,jdbcType=VARCHAR},
  subject_code = #{subjectCode,jdbcType=VARCHAR},
  subject_name = #{subjectName,jdbcType=VARCHAR},
  subject_unit = #{subjectUnit,jdbcType=VARCHAR},
  value = #{value,jdbcType=VARCHAR},
  actual = #{actual,jdbcType=VARCHAR},
  Cumulative_budget = #{cumulativeBudget,jdbcType=VARCHAR},
  Cumulative_actual = #{cumulativeActual,jdbcType=VARCHAR}
where MONTH = #{month,jdbcType=VARCHAR}
  and ptype = #{ptype,jdbcType=VARCHAR}
  and subject_parent_name = #{subjectParentName,jdbcType=VARCHAR}
  and year_num = #{yearNum,jdbcType=VARCHAR}
